mapper.select:
SELECT project_user.id, project_user.project_id, project_user.user_id, platform_user_info.user_job_num, project_user.user_pro_role, platform_user.user_name, project_user.pre_join_time, project_user.real_join_time, project_user.pre_out_time, project_user.real_out_time, project_user.pre_man_hour, project_user.user_status, pr1.role_name AS pro_role_name, pr2.role_name AS user_role_names FROM project_user LEFT JOIN platform_user ON project_user.user_id = platform_user.user_id LEFT JOIN platform_role pr1 ON project_user.user_pro_role = pr1.role_id LEFT JOIN platform_user_role ON platform_user_role.user_id = project_user.user_id LEFT JOIN platform_role pr2 ON pr2.role_id = platform_user_role.role_id LEFT JOIN platform_user_info ON platform_user_info.user_id = project_user.user_id WHERE project_user.project_id = #{projectId}
AND platform_user.user_name like concat(concat('%', #{userName}), '%')
AND project_user.user_status = #{userStatus}
AND project_user.user_pro_role = #{userProRoleIds}
AND pr2.role_id = #{roleId}
limit #{page}, #{size}

select count(1) from ( SELECT project_user.user_id FROM project_user LEFT JOIN platform_user ON project_user.user_id = platform_user.user_id LEFT JOIN platform_role pr1 ON project_user.user_pro_role = pr1.role_id LEFT JOIN platform_user_role ON platform_user_role.user_id = project_user.user_id LEFT JOIN platform_role pr2 ON pr2.role_id = platform_user_role.role_id LEFT JOIN platform_user_info ON platform_user_info.user_id = project_user.user_id WHERE project_user.project_id = #{projectId}
AND platform_user.user_name like concat(concat('%', #{userName}), '%')
AND project_user.user_status = #{userStatus}
AND project_user.user_pro_role = #{roleId}
AND pr2.role_id = #{userProRoleIds}
group by project_user.user_id) temps

