mapper.select:
SELECT

FROM version_citing_history
WHERE 1=1

  and version_id = #{versionCitingHistory.versionId}


  and project_id = #{versionCitingHistory.projectId}


  and demand_id = #{versionCitingHistory.demandId}


  and task_id = #{versionCitingHistory.taskId}


  and bug_id = #{versionCitingHistory.bugId}


  and test_case_id = #{versionCitingHistory.testCaseId}

 ORDER BY create_time DESC
