mapper.insert:
insert into version (id, project_id, version,
start_time, end_time, version_name,
version_type, create_user_id, create_time,
modify_user_id, modify_time, remarks
)
values (#{id,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT}, #{version,jdbcType=VARCHAR},
#{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{versionName,jdbcType=VARCHAR},
#{versionType,jdbcType=CHAR}, #{createUserId,jdbcType=VARCHAR}, now(),
#{modifyUserId,jdbcType=VARCHAR}, now(), #{remarks,jdbcType=LONGVARCHAR}
)

mapper.select:
SELECT version
FROM  version
WHERE project_id=#{projectId}
and version_type=#{versionType}
