select
from task t INNER JOIN platform_user a ON a.user_id = t.assign_user_id INNER JOIN platform_user b ON b.user_id = t.beassign_user_id INNER JOIN platform_user c ON c.user_id = t.create_user_id LEFT JOIN project_main p ON t.project_id = p.id LEFT JOIN demand d ON t.demand_id = d.id where t.task_parent_id = #{id}
and t.status = #{status,jdbcType=CHAR}
order by t.estimate_start_time,t.priority asc

select
from task t INNER JOIN platform_user a ON a.user_id = t.assign_user_id INNER JOIN platform_user b ON b.user_id = t.beassign_user_id INNER JOIN platform_user c ON c.user_id = t.create_user_id LEFT JOIN project_main p ON t.project_id = p.id LEFT JOIN demand d ON t.demand_id = d.id where t.task_parent_id = #{id} and t.beassign_user_id = #{beassignUserId,jdbcType=VARCHAR}
and t.status = #{status,jdbcType=CHAR}
order by t.estimate_start_time,t.priority asc

mapper.update:
update task t set 
t.status = #{status,jdbcType=CHAR},t.delay_describe = #{delayDescribe,jdbcType=VARCHAR},t.estimate_start_time = #{estimateStartTime,jdbcType=TIMESTAMP} 
where t.id = #{id,jdbcType=BIGINT}

update task t set 
t.status = #{status,jdbcType=CHAR},t.modify_user_id = #{modifyUserId,jdbcType=VARCHAR},t.modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where t.id = #{id,jdbcType=BIGINT}

update task t set 
t.assign_user_id = #{assignUserId,jdbcType=VARCHAR},
t.beassign_user_id = #{beassignUserId,jdbcType=VARCHAR}
where t.id = #{id,jdbcType=BIGINT}

update task t set 
t.task_parent_id = null
where t.id = #{id,jdbcType=BIGINT}

mapper.select:
select count(*) from task where status in ( 0 , 1 )

    and project_id = #{projectId}


    and beassign_user_id = #{userId}

SELECT

FROM task t
LEFT JOIN platform_user a ON t.assign_user_id = a.user_id
LEFT JOIN platform_user b ON t.beassign_user_id = b.user_id
LEFT JOIN platform_user c on t.create_user_id = c.user_id
LEFT JOIN project_main p ON t.project_id = p.id
LEFT JOIN demand d ON t.demand_id = d.id
WHERE t.beassign_user_id = #{ beassignUser.userId,jdbcType=CHAR}

    and t.task_num = #{taskNum,jdbcType=CHAR}


    and t.task_name LIKE  "%"#{taskName,jdbcType=CHAR}"%"


    and t.project_id = #{project.id,jdbcType=CHAR}


    and t.demand_id = #{demand.id,jdbcType=CHAR}

ORDER BY t.create_time DESC, t.priority DESC

SELECT
FROM task t LEFT JOIN task_log l ON t.id = l.task_id LEFT JOIN platform_user a ON a.user_id = t.assign_user_id LEFT JOIN platform_user b ON b.user_id = t.beassign_user_id LEFT JOIN platform_user c ON c.user_id = t.create_user_id LEFT JOIN project_main p ON t.project_id = p.id LEFT JOIN demand d ON t.demand_id = d.id WHERE t.task_type='2' AND l.operation_button = '提测' AND l.user_id = #{ beassignUser.userId,jdbcType=CHAR}
and t.task_num = #{taskNum,jdbcType=CHAR}
and t.task_name LIKE "%"#{taskName,jdbcType=CHAR}"%"
and t.project_id = #{project.id,jdbcType=CHAR}
and t.demand_id = #{demand.id,jdbcType=CHAR}
ORDER BY t.create_time DESC, t.priority DESC

SELECT
FROM task t LEFT JOIN platform_user a ON t.assign_user_id = a.user_id LEFT JOIN platform_user b ON t.beassign_user_id = b.user_id LEFT JOIN platform_user c on t.create_user_id = c.user_id LEFT JOIN project_main p ON t.project_id = p.id LEFT JOIN demand d ON t.demand_id = d.id WHERE t.beassign_user_id = #{ task.beassignUser.userId,jdbcType=CHAR} and status = #{task.status}
and t.task_num LIKE "%"#{task.taskNum,jdbcType=VARCHAR}"%"
and t.task_name LIKE "%"#{task.taskName,jdbcType=VARCHAR}"%"
and t.project_id = #{task.project.id,jdbcType=BIGINT}
and t.demand_id = #{task.demand.id,jdbcType=BIGINT}
UNION SELECT
FROM task t LEFT JOIN task_log l ON t.id = l.task_id LEFT JOIN platform_user a ON a.user_id = t.assign_user_id LEFT JOIN platform_user b ON b.user_id = t.beassign_user_id LEFT JOIN platform_user c ON c.user_id = t.create_user_id LEFT JOIN project_main p ON t.project_id = p.id LEFT JOIN demand d ON t.demand_id = d.id WHERE t.task_type= #{task.taskType} AND l.operation_button = #{operationButton} AND l.user_id = #{ task.beassignUser.userId,jdbcType=CHAR}
and t.task_num LIKE "%"#{task.taskNum,jdbcType=VARCHAR}"%"
and t.task_name LIKE "%"#{task.taskName,jdbcType=VARCHAR}"%"
and t.project_id = #{task.project.id,jdbcType=BIGINT}
and t.demand_id = #{task.demand.id,jdbcType=BIGINT}
group by t.id ORDER BY create_time DESC, priority DESC limit #{page} ,#{rows}

mapper.update:
   
    	update task t set 
		t.estimate_start_time = #{estimateStartTime,jdbcType=TIMESTAMP},
		t.delay_describe=#{delayDescribe}
		where t.id = #{id}

    	update task t set 
		t.status = 1 
		where t.id = #{taskId}

mapper.select:
SELECT
FROM task t LEFT JOIN platform_user a ON t.assign_user_id = a.user_id LEFT JOIN platform_user b ON t.beassign_user_id = b.user_id LEFT JOIN platform_user c on t.create_user_id = c.user_id LEFT JOIN project_main p ON t.project_id = p.id LEFT JOIN demand d ON t.demand_id = d.id WHERE t.status = 1 AND t.beassign_user_id = #{beassignUser.userId}
and t.task_num LIKE "%"#{taskNum,jdbcType=CHAR}"%"
and t.task_name LIKE "%"#{taskName,jdbcType=CHAR}"%"
and t.project_id = #{project.id,jdbcType=CHAR}
and t.demand_id = #{demand.id,jdbcType=CHAR}
ORDER BY t.create_time ASC, t.priority ASC
