mapper.select:
SELECT

from task t LEFT JOIN platform_user a ON t.assign_user_id = a.user_id
LEFT JOIN platform_user b ON t.beassign_user_id = b.user_id
LEFT JOIN platform_user c on t.create_user_id = c.user_id
LEFT JOIN project_main p ON t.project_id = p.id

    and t.task_type = #{task.taskType}
    and t.actual_end_time = #{task.actualEndTime}
    and t.task_name = #{task.taskName}
    and t.status = #{task.status}

        and beassign_user_id in

           id

    select

	from
		task t
	INNER JOIN platform_user a ON a.user_id = t.assign_user_id
	INNER JOIN platform_user b ON b.user_id = t.beassign_user_id
	INNER JOIN platform_user c ON c.user_id = t.create_user_id
	where t.beassign_user_id = #{beassignUserId,jdbcType=VARCHAR}

		and t.status = #{status,jdbcType=CHAR}


		and t.task_num = #{taskNum,jdbcType=CHAR}


		and t.task_name = #{taskName,jdbcType=CHAR}


		and t.project_id = #{project.proId,jdbcType=CHAR}


		and t.priority = #{priority,jdbcType=CHAR}


		and t.assign_user_id = #{assignUser.userId,jdbcType=CHAR}

	 order by t.estimate_start_time,t.priority asc

select
from task t INNER JOIN platform_user a ON a.user_id = t.assign_user_id INNER JOIN platform_user b ON b.user_id = t.beassign_user_id INNER JOIN platform_user c ON c.user_id = t.create_user_id where t.task_parent_id = #{id}
and t.status = #{status,jdbcType=CHAR}
order by t.estimate_start_time,t.priority asc

select
from task t INNER JOIN platform_user a ON a.user_id = t.assign_user_id INNER JOIN platform_user b ON b.user_id = t.beassign_user_id INNER JOIN platform_user c ON c.user_id = t.create_user_id where t.task_parent_id = #{id} and t.beassign_user_id = #{beassignUserId,jdbcType=VARCHAR}
and t.status = #{status,jdbcType=CHAR}
order by t.estimate_start_time,t.priority asc

mapper.update:
update task t set 
t.status = #{status,jdbcType=CHAR},t.delay_describe = #{delayDescribe,jdbcType=VARCHAR},t.estimate_start_time = #{estimateStartTime,jdbcType=TIMESTAMP} 
where t.id = #{id,jdbcType=BIGINT}

update task t set 
t.status = #{status,jdbcType=CHAR} 
where t.id = #{id,jdbcType=BIGINT}

mapper.select:
select count(*) from task where status in ( 0 , 1 , 3)

    and project_id = #{projectId}


    and beassign_user_id = #{userId}

select
from task t INNER JOIN platform_user a ON a.user_id = t.assign_user_id INNER JOIN platform_user b ON b.user_id = t.beassign_user_id INNER JOIN platform_user c ON c.user_id = t.create_user_id where t.beassign_user_id = #{beassignUserId,jdbcType=VARCHAR}
and t.demand_id = #{demandId,jdbcType=BIGINT}

mapper.update:
UPDATE task SET status = 1 WHERE

    id = #{aid}
 AND status = 2


    	update task t set 
		t.estimate_start_time = #{estimateStartTime,jdbcType=TIMESTAMP},
		t.delay_describe=#{delayDescribe} 
		where t.id = #{id}

    	update task t set 
		t.status = 1 
		where t.id = #{taskId}

update task


		status = #{status,jdbcType=CHAR},


		actual_end_time = #{actualEndTime,jdbcType=TIMESTAMP},


